### FUNCTIONS ###

def clearScreen(oled):
    oled.fill(0)
    oled.show()

def showBufferImage(oled, buffer):
    oled.fill(0)
    oled.blit(buffer, 0, 0, 0)
    oled.show()

# Plays the image sequence for the easter egg
def doEasterEgg():
    global TitleScreens, TextScreens, WIDTH, HEIGHT, oled_1, oled_2
    ILU0 = framebuf.FrameBuffer(ILUScreens[0], WIDTH, HEIGHT, framebuf.MONO_HLSB)
    ILU1 = framebuf.FrameBuffer(ILUScreens[1], WIDTH, HEIGHT, framebuf.MONO_HLSB)
    ILU2 = framebuf.FrameBuffer(ILUScreens[2], WIDTH, HEIGHT, framebuf.MONO_HLSB)
    ILU3 = framebuf.FrameBuffer(ILUScreens[3], WIDTH, HEIGHT, framebuf.MONO_HLSB)
    framerate = 0.75
    # Frame 1
    showBufferImage(oled_1, ILU0)
    time.sleep(framerate)
    # Frame 2
    showBufferImage(oled_1, ILU1)
    time.sleep(framerate)
    # Frame 3
    showBufferImage(oled_2, ILU2)
    time.sleep(framerate)
    # Frame 4
    showBufferImage(oled_2, ILU3)
    time.sleep(framerate)
    # Frame 3
    showBufferImage(oled_2, ILU2)
    time.sleep(framerate)
    # Frame 4
    showBufferImage(oled_2, ILU3)
    time.sleep(framerate)
    # Frame 3
    showBufferImage(oled_2, ILU2)
    time.sleep(framerate)
    # Frame 4
    showBufferImage(oled_2, ILU3)
    time.sleep(framerate)
    # Frame 3
    showBufferImage(oled_2, ILU2)
    time.sleep(3)    
    # Clear
    clearScreen(oled_1)
    clearScreen(oled_2)

# Determine if the current screen index (same as button index)
# is the correct continuation of the easter egg path or if it
# has broken the correct path and must reset the easter egg progress
def checkEasterEgg(currentScrnId):
    global EASTER_EGG_PATH, easterEggPos
    # Check if correct
    if (EASTER_EGG_PATH[easterEggPos+1] == currentScrnId):
        easterEggPos += 1
    # Otherwise reset if not duplicate
    elif (easterEggPos != -1 and EASTER_EGG_PATH[easterEggPos] != currentScrnId):
        easterEggPos = -1
    # Winning case
    if (easterEggPos == len(EASTER_EGG_PATH) - 1):
        doEasterEgg()
        easterEggPos = -1
        return True
    else:
        return False
        
        
        
