### MAIN RUNNING LOOP ####

while True:
    for btn in range(8):
        if mcp[btn+8].value() == 0:   #Active low
            if lastState[btn] == 1:
                if checkEasterEgg(btn):
                    continue
                imageBuffer = framebuf.FrameBuffer(TitleScreens[btn], WIDTH, HEIGHT, framebuf.MONO_HLSB)
                textBuffer = framebuf.FrameBuffer(TextScreens[btn], WIDTH, HEIGHT, framebuf.MONO_HLSB)
                showBufferImage(oled_1, imageBuffer)
                showBufferImage(oled_2, textBuffer)
                mcp[btn].output(1)
                lastState[btn] = 0
        elif mcp[btn+8].value() == 1:
            if lastState[btn] == 0:
                clearScreen(oled_1)
                clearScreen(oled_2)
                mcp[btn].output(0)
                lastState[btn] = 1
